<link rel="stylesheet" href="{{ asset('css/variables.css') }}">

<style>
    body {
        margin: 0;
        padding: 0;
        box-sizing: border-box;
        font-family: Arial, sans-serif;
    }
    .navbar {
        display: flex;
        align-items: center;
        justify-content: space-between;
        padding: 10px 20px;
        background: var(--white);
    }
    .logo {
        display: flex;
        align-items: center;
        font-size: 24px;
        font-weight: bold;
    }
    .search-bar {
        display: flex;
        align-items: center;
        width: 50%;
        background: var(--subtle-white);
        border-radius: 5px;
        padding: 5px;
    }
    .search-bar input {
        border: none;
        background: var(--subtle-white);
        flex: 1;
        outline: none;
        height: 31px;
        padding: 0 10px;
        font-size: 12.9px;
        color: var(--gray);
        border-radius: 4px 0 0 4px;
    }
    .search-bar button {
        width: 55px;
        height: 31px;
        background: var(--primary-blue);
        border: none;
        color: var(--white);
        cursor: pointer;
        border-radius: 0 4px 4px 0;
    }
    .menu {
        display: flex;
        align-items: center;
    }
    .user {
        text-decoration: none;
        color: var(--dark-gray);
        margin: 0 10px 0 20px;
    }
    .cart-container {
        display: flex;
        align-items: center;
        gap: 8px;
        position: relative;
    }
    .cart-badge {
        position: absolute;
        top: 12px;
        left: 12px;
        background: var(--yellow);
        border-radius: 10px;
        padding: 2px 5px;
        font-size: 11px;
        color: var(--dark-gray);
    }
    .cart {
        display: flex;
        align-items: center;
        gap: 5px;
        cursor: pointer;
    }
</style>

